import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ArrowLeft, User, Clock, Calendar, BarChart2 } from 'lucide-react';
import { getFoniciData } from './data'; // Assicurati di creare questa funzione in data.js

const Container = styled.div`
  background-color: #F0F9FF;
  min-height: 100vh;
  padding: 24px;
  font-family: 'Helvetica Neue', Arial, sans-serif;
`;

const Content = styled.div`
  max-width: 800px;
  margin: 0 auto;
`;

const Title = styled.h1`
  font-size: 32px;
  font-weight: bold;
  text-align: center;
  margin-bottom: 32px;
  color: #1F2937;
`;

const BackButton = styled.button`
  background: #4B5563;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 10px 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  font-size: 16px;
  margin-bottom: 24px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #374151;
  }
`;

const StatGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 24px;
`;

const StatCard = styled.div`
  background-color: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const StatTitle = styled.h2`
  font-size: 18px;
  font-weight: 600;
  color: #4B5563;
  margin-bottom: 12px;
  display: flex;
  align-items: center;
`;

const StatValue = styled.p`
  font-size: 24px;
  font-weight: bold;
  color: #1F2937;
`;

const StatisticheFonici = ({ setView }) => {
  const [foniciData, setFoniciData] = useState(null);

  useEffect(() => {
    const data = getFoniciData(); // Implementa questa funzione in data.js
    setFoniciData(data);
  }, []);

  if (!foniciData) {
    return <div>Caricamento...</div>;
  }

  return (
    <Container>
      <Content>
        <BackButton onClick={() => setView('main')}>
          <ArrowLeft size={20} style={{ marginRight: '8px' }} />
          Torna alla Dashboard
        </BackButton>

        <Title>Statistiche Fonici</Title>

        <StatGrid>
          <StatCard>
            <StatTitle><User size={20} style={{ marginRight: '8px' }} /> Numero totale di fonici</StatTitle>
            <StatValue>{foniciData.totaleFonici}</StatValue>
          </StatCard>
          <StatCard>
            <StatTitle><Clock size={20} style={{ marginRight: '8px' }} /> Media turni per fonico</StatTitle>
            <StatValue>{foniciData.mediaTurniPerFonico.toFixed(2)}</StatValue>
          </StatCard>
          <StatCard>
            <StatTitle><Calendar size={20} style={{ marginRight: '8px' }} /> Fonico pi√π attivo</StatTitle>
            <StatValue>{foniciData.fonicopiuAttivo.nome} ({foniciData.fonicopiuAttivo.turni} turni)</StatValue>
          </StatCard>
          <StatCard>
            <StatTitle><BarChart2 size={20} style={{ marginRight: '8px' }} /> Turni totali questo mese</StatTitle>
            <StatValue>{foniciData.turniTotaliMese}</StatValue>
          </StatCard>
        </StatGrid>
      </Content>
    </Container>
  );
};

export default StatisticheFonici;
