import React, { useState, useEffect, useMemo, useCallback } from 'react';
import styled from 'styled-components';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { Share2 } from 'lucide-react';
import { 
  getYearlyData, 
  getCurrentYearAndMonth,
  getAvailableYears
} from './data';

// Styled components
const ViewContainer = styled.div`
  background-color: #F0F9FF;
  min-height: 100vh;
  padding: 24px;
  font-family: 'Helvetica Neue', Arial, sans-serif;
`;

const ViewContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const ViewTitle = styled.h1`
  font-size: 32px;
  font-weight: bold;
  text-align: center;
  margin-bottom: 32px;
  color: #1F2937;
`;

const ChartContainer = styled.div`
  background-color: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 24px;
  margin-bottom: 24px;
`;

const AnalysisSection = styled.div`
  background-color: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 24px;
  margin-bottom: 24px;
`;

const AnalysisTitle = styled.h2`
  font-size: 24px;
  font-weight: bold;
  margin-bottom: 16px;
  color: #1F2937;
`;

const AnalysisText = styled.p`
  font-size: 16px;
  line-height: 1.5;
  color: #4B5563;
  white-space: pre-line;
`;

const LegendContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 20px;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  margin-right: 20px;
  margin-bottom: 10px;
`;

const LegendColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  margin-right: 8px;
  background-color: ${props => props.color};
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 24px;
`;

const Button = styled.button`
  background-color: #3B82F6;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;
  display: flex;
  align-items: center;

  &:hover {
    background-color: #2563EB;
  }

  svg {
    margin-right: 8px;
  }
`;

// Helper functions
const getMonthName = (monthNumber) => {
  const monthNames = [
    'Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno',
    'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'
  ];
  return monthNames[monthNumber - 1];
};

const formatNumber = (num) => new Intl.NumberFormat('it-IT').format(Math.round(num));

const generateColorPalette = (numColors) => {
  const hueStep = 360 / numColors;
  return Array.from({ length: numColors }, (_, i) => 
    `hsl(${i * hueStep}, 70%, 50%)`
  );
};

const PerformanceTrendView = ({ setView }) => {
  const [allYearsData, setAllYearsData] = useState({});
  const [yearConclusions, setYearConclusions] = useState('');
  const [prediction, setPrediction] = useState('');

  const availableYears = useMemo(() => getAvailableYears().map(y => parseInt(y.value)), []);
  const colorPalette = useMemo(() => generateColorPalette(availableYears.length), [availableYears]);
  const { currentYear, currentMonth } = getCurrentYearAndMonth();

  const generateYearConclusions = useCallback((year, currentMonth, yearData, allYearsData) => {
    const month = currentMonth > 1 ? currentMonth - 1 : 12;
    const yearToAnalyze = month === 12 ? year - 1 : year;
    const currentYearData = yearData[yearToAnalyze] || {};

    const totalTurni = Object.values(currentYearData)
      .slice(0, month)
      .reduce((sum, monthData) => sum + (monthData?.totaleTurni || 0), 0);

    const monthsWithData = Object.values(currentYearData)
      .slice(0, month)
      .filter(monthData => monthData && monthData.totaleTurni > 0).length;

    const averageMonthlyTurni = monthsWithData > 0 ? totalTurni / monthsWithData : 0;

    // Calcolo delle statistiche per gli anni precedenti
    const previousYearsData = Object.entries(allYearsData)
      .filter(([y]) => parseInt(y) < yearToAnalyze)
      .map(([year, data]) => {
        const yearTotal = Object.values(data)
          .slice(0, month)
          .reduce((sum, monthData) => sum + (monthData?.totaleTurni || 0), 0);
        return { year: parseInt(year), totalTurni: yearTotal };
      });

    const avgPreviousYears = previousYearsData.reduce((sum, y) => sum + y.totalTurni, 0) / previousYearsData.length;

    let analysis = `Considerazioni sull'anno ${yearToAnalyze} (fino a ${getMonthName(month)}):\n\n`;
    analysis += `Nel periodo analizzato, sono stati effettuati un totale di ${formatNumber(totalTurni)} turni, `;
    analysis += `con una media mensile di ${formatNumber(averageMonthlyTurni)} turni. `;

    const percentDiff = ((totalTurni - avgPreviousYears) / avgPreviousYears) * 100;
    analysis += `Questo rappresenta un ${percentDiff > 0 ? 'aumento' : 'diminuzione'} del ${formatNumber(Math.abs(percentDiff))}% `;
    analysis += `rispetto alla media di tutti gli anni precedenti nello stesso periodo.\n\n`;

    // Nuova analisi del trend
    analysis += `Analisi dettagliata:\n`;
    const lastYear = yearToAnalyze - 1;
    const lastYearData = allYearsData[lastYear] || {};
    let growthMonths = [];
    let declineMonths = [];
    let consecutiveGrowth = 0;
    let consecutiveDecline = 0;
    let maxConsecutiveGrowth = 0;
    let maxConsecutiveDecline = 0;

    for (let i = 0; i < month; i++) {
      const currentMonthTurni = currentYearData[i]?.totaleTurni || 0;
      const lastYearMonthTurni = lastYearData[i]?.totaleTurni || 0;
      const monthlyDiff = currentMonthTurni - lastYearMonthTurni;
      const monthlyPercentDiff = (monthlyDiff / lastYearMonthTurni) * 100;

      if (monthlyDiff > 0) {
        growthMonths.push({ month: i, diff: monthlyPercentDiff });
        consecutiveGrowth++;
        consecutiveDecline = 0;
      } else if (monthlyDiff < 0) {
        declineMonths.push({ month: i, diff: monthlyPercentDiff });
        consecutiveDecline++;
        consecutiveGrowth = 0;
      }

      maxConsecutiveGrowth = Math.max(maxConsecutiveGrowth, consecutiveGrowth);
      maxConsecutiveDecline = Math.max(maxConsecutiveDecline, consecutiveDecline);
    }

    growthMonths.sort((a, b) => b.diff - a.diff);
    declineMonths.sort((a, b) => a.diff - b.diff);

    if (growthMonths.length > 0) {
      analysis += `I mesi con la maggiore crescita rispetto all'anno precedente sono stati:\n`;
      growthMonths.slice(0, 3).forEach(m => {
        analysis += `- ${getMonthName(m.month + 1)} con un aumento del ${formatNumber(m.diff)}%\n`;
      });
    }

    if (declineMonths.length > 0) {
      analysis += `\nI mesi con la maggiore diminuzione rispetto all'anno precedente sono stati:\n`;
      declineMonths.slice(0, 3).forEach(m => {
        analysis += `- ${getMonthName(m.month + 1)} con una diminuzione del ${formatNumber(Math.abs(m.diff))}%\n`;
      });
    }

    analysis += `\nLa performance ha mostrato una crescita continua per un massimo di ${maxConsecutiveGrowth} mesi consecutivi `;
    analysis += `e una diminuzione continua per un massimo di ${maxConsecutiveDecline} mesi consecutivi.\n`;

    // Analisi stagionale
    analysis += `\nAnalisi stagionale:\n`;
  const quarterlyData = [
    [0, 1, 2], // primo trimestre
    [3, 4, 5], // secondo trimestre
    [6, 7, 8], // terzo trimestre
    [9, 10, 11] // quarto trimestre
  ].map(months => 
    months.map(m => currentYearData[m]?.totaleTurni || 0).filter(t => t > 0)
  );

  const quarterlyAverages = quarterlyData.map(q => q.length > 0 ? q.reduce((a, b) => a + b, 0) / q.length : null);

  const quarters = ['primo', 'secondo', 'terzo', 'quarto'];
  quarterlyAverages.forEach((avg, index) => {
    if (avg !== null) {
      const percentDiff = ((avg - averageMonthlyTurni) / averageMonthlyTurni) * 100;
      analysis += `Il ${quarters[index]} trimestre mostra una media di ${formatNumber(avg)} turni al mese, `;
      analysis += `${percentDiff > 0 ? 'superiore' : 'inferiore'} del ${formatNumber(Math.abs(percentDiff))}% rispetto alla media annuale.\n`;
    }
  });


    // Conclusione finale
    const allYearsTotalTurni = Object.values(allYearsData).map(yearData => 
      Object.values(yearData).reduce((sum, month) => sum + (month?.totaleTurni || 0), 0)
    );
    const sortedTotalTurni = [...allYearsTotalTurni].sort((a, b) => b - a);
    const currentYearRank = sortedTotalTurni.indexOf(totalTurni) + 1;
    const isTopPerformer = currentYearRank <= Math.ceil(sortedTotalTurni.length * 0.2);

    analysis += `\nConclusione:\nCon le informazioni attuali possiamo determinare che la produttività dell'anno in corso è `;
    analysis += percentDiff > 0 ? 'superiore' : 'inferiore';
    analysis += ` alla media degli anni passati. `;

    if (currentYearRank === 1) {
      analysis += `Questo è attualmente il miglior anno di sempre in termini di produttività.`;
    } else if (isTopPerformer) {
      analysis += `Questo è uno dei migliori anni, posizionandosi al ${currentYearRank}° posto nella classifica storica.`;
    } else {
      analysis += `La performance di quest'anno si posiziona al ${currentYearRank}° posto nella classifica storica.`;
    }

    return analysis;
  }, []);

  const makePrediction = useCallback((year, month, allYearsData) => {
    // Il codice per makePrediction rimane invariato
  }, [availableYears]);

  const formatChartData = useCallback(() => {
    // Il codice per formatChartData rimane invariato
  }, [allYearsData, availableYears, currentYear, currentMonth]);

  useEffect(() => {
    const fetchData = async () => {
      const yearsData = {};
      for (const year of availableYears) {
        yearsData[year] = await getYearlyData(year);
      }
      setAllYearsData(yearsData);

      setYearConclusions(generateYearConclusions(currentYear, currentMonth, yearsData, yearsData));
      setPrediction(makePrediction(currentYear, currentMonth, yearsData));
    };

    fetchData();
  }, [currentYear, currentMonth, availableYears, generateYearConclusions, makePrediction]);

  const chartData = useMemo(() => formatChartData(), [formatChartData]);

  const handleShare = () => {
    const content = `Performance Trend\n\n${yearConclusions}\n\n${prediction}`;
    navigator.clipboard.writeText(content).then(() => {
      alert('Contenuto copiato negli appunti. Ora puoi condividerlo!');
    }).catch(err => {
      console.error('Errore nel copiare il contenuto: ', err);
      alert('Si è verificato un errore nel copiare il contenuto. Per favore, prova a copiarlo manualmente.');
    });
  };

  return (
    <ViewContainer>
      <ViewContent>
        <ViewTitle>Performance Trend</ViewTitle>
        <ChartContainer>
          <AnalysisTitle>Trend annuale</AnalysisTitle>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              {availableYears.map((year, index) => {
                const isCurrentYear = year === currentYear;
                if (isCurrentYear) {
                  return (
                    <React.Fragment key={year}>
                      {/* Linea per i dati reali dell'anno corrente */}
                      <Line
                        type="monotone"
                        dataKey={(dataPoint) => dataPoint[`${year}IsPrediction`] ? undefined : dataPoint[year]}
                        stroke={colorPalette[index]}
                        strokeWidth={3}
                        dot={false}
                        connectNulls={true}
                      />
                      {/* Linea tratteggiata per i dati previsionali dell'anno corrente */}
                      <Line
                        type="monotone"
                        dataKey={(dataPoint) => {
                          if (dataPoint[`${year}IsPrediction`]) {
                            return dataPoint[year];
                          }
                          // Restituisce il valore dell'ultimo mese reale per connettere le linee
                          const lastRealMonth = chartData.findIndex(d => d[`${year}IsPrediction`]) - 1;
                          return dataPoint === chartData[lastRealMonth] ? dataPoint[year] : undefined;
                        }}
                        stroke={colorPalette[index]}
                        strokeWidth={3}
                        strokeDasharray="5 5"
                        dot={false}
                        connectNulls={true}
                      />
                    </React.Fragment>
                  );
                } else {
                  return (
                    <Line
                      key={year}
                      type="monotone"
                      dataKey={year.toString()}
                      stroke={colorPalette[index]}
                      strokeWidth={1}
                      dot={false}
                    />
                  );
                }
              })}
            </LineChart>
          </ResponsiveContainer>
          <LegendContainer>
            {availableYears.map((year, index) => (
              <LegendItem key={year}>
                <LegendColor color={colorPalette[index]} />
                <span>{year === currentYear ? `${year} (linea continua: dati reali, tratteggiata: previsione)` : year}</span>
              </LegendItem>
            ))}
          </LegendContainer>
        </ChartContainer>
        <AnalysisSection>
          <AnalysisTitle>Considerazioni sull'anno in corso</AnalysisTitle>
          <AnalysisText>{yearConclusions}</AnalysisText>
        </AnalysisSection>
        <AnalysisSection>
          <AnalysisTitle>Previsione per i prossimi mesi</AnalysisTitle>
          <AnalysisText>{prediction}</AnalysisText>
        </AnalysisSection>
        <ButtonContainer>
          <Button onClick={() => setView('main')}>Torna alla Dashboard</Button>
          <Button onClick={handleShare}>
            <Share2 size={20} />
            Condividi
          </Button>
        </ButtonContainer>
      </ViewContent>
    </ViewContainer>
  );
};

export default PerformanceTrendView;
