import React from 'react';
import styled from 'styled-components';

const LandingContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #F0F9FF;
  padding: 20px;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 3rem;
  color: #1F2937;
  margin-bottom: 20px;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  color: #4B5563;
  max-width: 600px;
  margin-bottom: 40px;
`;

const EnterButton = styled.button`
  background-color: #3B82F6;
  color: white;
  border: none;
  padding: 15px 30px;
  font-size: 1.2rem;
  border-radius: 8px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #2563EB;
  }
`;

const LandingPage = ({ onEnter }) => {
  return (
    <LandingContainer>
      <Title>Benvenuti nello Studio Pumaisdue</Title>
      <Subtitle>
        Esplora le statistiche e le performance del nostro studio di registrazione audio.
        Analizza i trend, confronta i dati e ottimizza la gestione dei turni.
      </Subtitle>
      <EnterButton onClick={onEnter}>Entra nella Dashboard</EnterButton>
    </LandingContainer>
  );
};

export default LandingPage;
