import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ArrowLeft } from 'lucide-react';
import { getFoniciData } from './data';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const Container = styled.div`
  background-color: #F0F9FF;
  min-height: 100vh;
  padding: 24px;
  font-family: 'Helvetica Neue', Arial, sans-serif;
`;

const Content = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const Title = styled.h1`
  font-size: 32px;
  font-weight: bold;
  text-align: center;
  margin-bottom: 32px;
  color: #1F2937;
`;

const BackButton = styled.button`
  background: #4B5563;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 10px 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  font-size: 16px;
  margin-bottom: 24px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #374151;
  }
`;

const ClassificheGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 24px;
`;

const ClassificaCard = styled.div`
  background-color: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const ClassificaTitle = styled.h2`
  font-size: 20px;
  font-weight: 600;
  color: #4B5563;
  margin-bottom: 16px;
  text-align: center;
`;

const ClassificaList = styled.ol`
  list-style-position: inside;
  padding: 0;
`;

const ChartsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 24px;
  margin-top: 32px;
`;

const ChartCard = styled.div`
  background-color: white;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  height: 400px;
`;

const ChartTitle = styled.h2`
  font-size: 20px;
  font-weight: 600;
  color: #4B5563;
  margin-bottom: 16px;
  text-align: center;
`;

const ClassificaItem = styled.li`
  font-size: 16px;
  margin-bottom: 8px;
  display: grid;
  grid-template-columns: 30px 1fr 80px 80px;
  align-items: center;

  &:nth-child(1) { font-weight: bold; color: #FFA500; }
  &:nth-child(2) { font-weight: bold; color: #A9A9A9; }
  &:nth-child(3) { font-weight: bold; color: #CD7F32; }
`;

const TurniSpan = styled.span`
  text-align: right;
`;

const DifferenzialeSpan = styled.span`
  font-size: 14px;
  color: #EF4444;
  text-align: right;
`;

const StatisticheFonici = ({ setView }) => {
  const [foniciData, setFoniciData] = useState(null);

  useEffect(() => {
    const data = getFoniciData();
    setFoniciData(data);
  }, []);

  if (!foniciData) {
    return <div>Caricamento...</div>;
  }

  const renderClassifica = (data, title) => (
    <ClassificaCard>
      <ClassificaTitle>{title}</ClassificaTitle>
      <ClassificaList>
        {data.map((fonico, index) => {
          const differenziale = index > 0 ? data[0].turni - fonico.turni : null;
          const showDifferenziale = (title === "Utilizzo Ultimo Quadrimestre" || title === "Utilizzo Ultimo Anno") && index > 0;
          return (
            <ClassificaItem key={fonico.nome}>
              <span>{index + 1}.</span>
              <span>{fonico.nome}</span>
              <TurniSpan>{fonico.turni} turni</TurniSpan>
              {showDifferenziale && (
                <DifferenzialeSpan>-{differenziale}</DifferenzialeSpan>
              )}
              {!showDifferenziale && <span></span>} {/* Placeholder per mantenere l'allineamento */}
            </ClassificaItem>
          );
        })}
      </ClassificaList>
    </ClassificaCard>
  );

  const renderChart = (data, title) => (
    <ChartCard>
      <ChartTitle>{title}</ChartTitle>
      <ResponsiveContainer width="100%" height="90%">
        <BarChart
          data={data}
          layout="vertical"
          margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis type="number" />
          <YAxis dataKey="nome" type="category" width={100} />
          <Tooltip />
          <Bar dataKey="turni" fill="#8884d8" />
        </BarChart>
      </ResponsiveContainer>
    </ChartCard>
  );

  return (
    <Container>
      <Content>
        <BackButton onClick={() => setView('main')}>
          <ArrowLeft size={20} style={{ marginRight: '8px' }} />
          Torna alla Dashboard
        </BackButton>

        <Title>Statistiche Fonici</Title>

        <ClassificheGrid>
          {renderClassifica(foniciData.ultimoMese, "Utilizzo Ultimo Mese")}
          {renderClassifica(foniciData.ultimoQuadrimestre, "Utilizzo Ultimo Quadrimestre")}
          {renderClassifica(foniciData.ultimoAnno, "Utilizzo Ultimo Anno")}
        </ClassificheGrid>

        <ChartsGrid>
          {renderChart(foniciData.ultimoMese, "Grafico Ultimo Mese")}
          {renderChart(foniciData.ultimoQuadrimestre, "Grafico Ultimo Quadrimestre")}
          {renderChart(foniciData.ultimoAnno, "Grafico Ultimo Anno")}
        </ChartsGrid>
      </Content>
    </Container>
  );
};

export default StatisticheFonici;
