import React, { useState, useEffect, useMemo } from 'react';
import styled from 'styled-components';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { 
  getYearlyData, 
  getCurrentYearAndMonth,
  getAvailableYears
} from './data';

// Styled components
const ViewContainer = styled.div`
  background-color: #F0F9FF;
  min-height: 100vh;
  padding: 24px;
  font-family: 'Helvetica Neue', Arial, sans-serif;
`;

const ViewContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const ViewTitle = styled.h1`
  font-size: 32px;
  font-weight: bold;
  text-align: center;
  margin-bottom: 32px;
  color: #1F2937;
`;

const ChartContainer = styled.div`
  background-color: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 24px;
  margin-bottom: 24px;
`;

const AnalysisSection = styled.div`
  background-color: white;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 24px;
  margin-bottom: 24px;
`;

const AnalysisTitle = styled.h2`
  font-size: 24px;
  font-weight: bold;
  margin-bottom: 16px;
  color: #1F2937;
`;

const AnalysisText = styled.p`
  font-size: 16px;
  line-height: 1.5;
  color: #4B5563;
  white-space: pre-line;
`;

const LegendContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 20px;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  margin-right: 20px;
  margin-bottom: 10px;
`;

const LegendColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  margin-right: 8px;
  background-color: ${props => props.color};
`;

const BackButton = styled.button`
  background-color: #3B82F6;
  color: white;
  border: none;
  border-radius: 8px;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #2563EB;
  }
`;

// Helper functions
const getMonthName = (monthNumber) => {
  const monthNames = [
    'Gennaio', 'Febbraio', 'Marzo', 'Aprile', 'Maggio', 'Giugno',
    'Luglio', 'Agosto', 'Settembre', 'Ottobre', 'Novembre', 'Dicembre'
  ];
  return monthNames[monthNumber - 1];
};

const formatNumber = (num) => new Intl.NumberFormat('it-IT').format(num);

const generateColorPalette = (numColors) => {
  const hueStep = 360 / numColors;
  return Array.from({ length: numColors }, (_, i) => 
    `hsl(${i * hueStep}, 70%, 50%)`
  );
};

const generateYearConclusions = (year, month, yearData, allYearsData) => {
  const currentYearData = yearData[year] || {};
  const totalTurni = Object.values(currentYearData).reduce((sum, month) => sum + (month.totaleTurni || 0), 0);
  const monthsWithData = Object.values(currentYearData).filter(month => month && month.totaleTurni > 0).length;
  const averageMonthlyTurni = monthsWithData > 0 ? totalTurni / monthsWithData : 0;

  const lastYear = year - 1;
  const lastYearData = allYearsData[lastYear] || {};
  const lastYearTotalTurni = Object.values(lastYearData).reduce((sum, month) => sum + (month.totaleTurni || 0), 0);
  const percentageChange = lastYearTotalTurni > 0 ? ((totalTurni - lastYearTotalTurni) / lastYearTotalTurni) * 100 : 0;

  const monthsData = Object.entries(currentYearData)
    .map(([monthIndex, data]) => ({ month: parseInt(monthIndex) + 1, turni: data.totaleTurni || 0 }))
    .filter(data => data.month <= month);
  const maxMonth = monthsData.reduce((max, current) => current.turni > max.turni ? current : max, { turni: 0 });
  const minMonth = monthsData.reduce((min, current) => (current.turni < min.turni && current.turni > 0) ? current : min, { turni: Infinity });

  let conclusions = `Conclusioni per l'anno ${year} (fino a ${getMonthName(month)}):\n\n`;

  conclusions += `• Totale turni effettuati: ${formatNumber(totalTurni)}\n`;
  conclusions += `• Media mensile: ${formatNumber(averageMonthlyTurni.toFixed(2))} turni\n`;

  if (percentageChange !== 0) {
    conclusions += `• Variazione rispetto all'anno precedente: ${percentageChange > 0 ? '+' : ''}${percentageChange.toFixed(2)}%\n`;
  }

  conclusions += `• Mese più intenso: ${getMonthName(maxMonth.month)} con ${formatNumber(maxMonth.turni)} turni\n`;
  conclusions += `• Mese meno intenso: ${getMonthName(minMonth.month)} con ${formatNumber(minMonth.turni)} turni\n\n`;

  const trend = monthsData.slice(-3).every((data, index, array) => 
    index === 0 || data.turni >= array[index - 1].turni
  ) ? "crescente" : monthsData.slice(-3).every((data, index, array) => 
    index === 0 || data.turni <= array[index - 1].turni
  ) ? "decrescente" : "stabile";

  conclusions += `Il trend degli ultimi tre mesi è ${trend}. `;

  if (month < 12) {
    const remainingMonths = 12 - month;
    const projectedTotal = totalTurni + (averageMonthlyTurni * remainingMonths);
    conclusions += `Se il trend attuale continua, si prevede di raggiungere circa ${formatNumber(projectedTotal.toFixed(0))} turni entro la fine dell'anno.\n\n`;
  }

  const previousYearsAverage = Object.entries(allYearsData)
    .filter(([y]) => parseInt(y) < year)
    .reduce((sum, [_, yearData]) => {
      const yearTotal = Object.values(yearData).reduce((total, month) => total + (month.totaleTurni || 0), 0);
      return sum + yearTotal;
    }, 0) / (Object.keys(allYearsData).length - 1);

  const comparisonWithAverage = ((totalTurni / previousYearsAverage) - 1) * 100;

  conclusions += `Rispetto alla media degli anni precedenti, l'anno corrente sta registrando una ${comparisonWithAverage > 0 ? 'crescita' : 'diminuzione'} del ${Math.abs(comparisonWithAverage).toFixed(2)}% nel numero di turni.`;

  return conclusions;
};

const PerformanceTrendView = ({ setView }) => {
  const [allYearsData, setAllYearsData] = useState({});
  const [yearConclusions, setYearConclusions] = useState('');
  const [prediction, setPrediction] = useState('');

  const availableYears = useMemo(() => getAvailableYears().map(y => parseInt(y.value)), []);
  const colorPalette = useMemo(() => generateColorPalette(availableYears.length), [availableYears]);

  const { currentYear, currentMonth } = getCurrentYearAndMonth();

  useEffect(() => {
    const yearsData = {};
    availableYears.forEach(year => {
      yearsData[year] = getYearlyData(year);
    });
    setAllYearsData(yearsData);

    setYearConclusions(generateYearConclusions(currentYear, currentMonth, yearsData, yearsData));
    setPrediction(makePrediction(currentYear, currentMonth, yearsData));
  }, [currentYear, currentMonth, availableYears]);

  const makePrediction = (year, month, allYearsData) => {
    const futurePredictions = [];
    for (let i = month + 1; i <= 12; i++) {
      let totalTurni = 0;
      let yearsCount = 0;
      availableYears.forEach(y => {
        const yearData = allYearsData[y];
        if (yearData && yearData[i - 1] && yearData[i - 1].totaleTurni > 0) {
          totalTurni += yearData[i - 1].totaleTurni;
          yearsCount++;
        }
      });
      if (yearsCount > 0) {
        const averageTurni = totalTurni / yearsCount;
        futurePredictions.push(`• ${getMonthName(i)}: ${formatNumber(averageTurni.toFixed(0))} turni`);
      }
    }

    return `
      Previsione per i mesi rimanenti dell'anno ${year}, basata sulla media degli anni precedenti:
      ${futurePredictions.join('\n')}
    `;
  };

  const formatChartData = () => {
    const chartData = [];
    for (let month = 0; month < 12; month++) {
      const monthData = { month: getMonthName(month + 1) };
      availableYears.forEach(year => {
        const yearData = allYearsData[year];
        if (yearData && yearData[month]) {
          monthData[year] = yearData[month].totaleTurni;
        }
      });
      chartData.push(monthData);
    }
    return chartData;
  };

  return (
    <ViewContainer>
      <ViewContent>
        <ViewTitle>Performance Trend</ViewTitle>
        <ChartContainer>
          <AnalysisTitle>Trend annuale</AnalysisTitle>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={formatChartData()}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              {availableYears.map((year, index) => (
                <Line
                  key={year}
                  type="monotone"
                  dataKey={year.toString()}
                  stroke={colorPalette[index]}
                  strokeWidth={year === currentYear ? 3 : 1}
                  dot={false}
                />
              ))}
            </LineChart>
          </ResponsiveContainer>
          <LegendContainer>
            {availableYears.map((year, index) => (
              <LegendItem key={year}>
                <LegendColor color={colorPalette[index]} />
                <span>{year}</span>
              </LegendItem>
            ))}
          </LegendContainer>
        </ChartContainer>
        <AnalysisSection>
          <AnalysisTitle>Conclusioni sull'anno in corso</AnalysisTitle>
          <AnalysisText>{yearConclusions}</AnalysisText>
        </AnalysisSection>
        <AnalysisSection>
          <AnalysisTitle>Previsione per i prossimi mesi</AnalysisTitle>
          <AnalysisText>{prediction}</AnalysisText>
        </AnalysisSection>
        <BackButton onClick={() => setView('main')}>Torna alla Dashboard</BackButton>
      </ViewContent>
    </ViewContainer>
  );
};

export default PerformanceTrendView;
